sources.detail\\detail_css\\detail.scss:
* {
    margin: 0;
    padding: 0;
}

li {
    list-style: none;
}

a {
    text-decoration: none;
    color: #000;
}


/************ color **************/
$color: #fcf9ee;

$font-family: 'KOTRA_GOTHIC';

button {
    background: transparent;
    border: none;
    cursor: pointer;
}

p,
span,
ul,
li,
div,
button,
a {
    letter-spacing: 1px;
    font-family: 'Bookk Myungjo';
}

body {
    background-color: $color;
}

.rolling_banner {
    overflow: hidden;
    display: flex;
    width: 100%;
    background: #000;

    .rolling {
        display: flex;
        height: 40px;
        background-color: #000;
        z-index: 9999;

        >li {
            font-size: 14px;
            width: 1300px;
            color: #fff;
            line-height: 40px;
            font-family: 'KOTRA_GOTHIC ';


        }
    }
}

@keyframes flowRolling {
    0% {
        transform: translateX(0);
    }

    100% {
        transform: translateX(-100%);
    }
}

.header {
    position: absolute;
    width: 100%;
    height: 80px;
    z-index: 1;
    transition: 0.3s;
    border-bottom: 1px solid #222;

    &:hover {
        /*    height: 150px; */
        background-color: $color;

        .header_inner {
            .logo {
                >a {
                    color: #222;
                }
            }

        }

        .header_inner .lnb .menu>a {
            color: #222;
            font-weight: bold;
        }

        .header_inner .gnb {
            filter: invert(100%);
            /* 블랙만들기 */
        }

        .lnb {
            .menu {
                a {
                    color: #222;
                }
            }
        }

    }



    .header_inner {
        position: relative;
        width: 1300px;
        margin: 0 auto;
        display: flex;


        .logo {
            color: #222;
            padding: 10px 0;
            margin-left: 45px;
            width: 185px;
            text-align: center;
            /*  &:hover{color: #222;} */
            cursor: pointer;

            >a {
                color: #222;

                p {
                    font-size: 32px;
                    font-weight: bold;
                    /*  letter-spacing: 3px; */
                }

                span {
                    font-size: 12px;
                    font-weight: bold;
                    letter-spacing: 8px;
                }
            }
        }



        /************ lnb_menu ********/
        .lnb {
            width: 800px;
            position: relative;

            >div {
                font-weight: bold;
            }

            .menu {
                position: absolute;
                margin-left: 45px;
                margin-top: 40px;
                float: left;

                &:hover a {
                    color: #666;
                }

                &:hover ul {
                    display: block;
                }

                >a {
                    font-size: 20px;
                }

                ul {
                    overflow: hidden;
                    display: none;
                    margin-top: 30px;

                    li {
                        float: left;
                        margin-right: 50px;

                        a {
                            font-size: 14px;
                            font-family: $font-family;

                            &:hover {
                                color: #222;
                            }
                        }



                    }
                }
            }

            .new {
                left: 0px;
            }

            .sent {
                left: 120px
            }

            .shop {
                left: 240px
            }

            .about {
                left: 360px
            }
        }
    }

    /*************** gnb  *****************/
    .gnb {
        position: absolute;
        right: 20px;
        margin-top: 40px;
        width: 140px;
        filter: invert(100%);

        li {
            float: left;
            margin-right: 25px;
            /*   cursor: pointer; */
            cursor: pointer;
            transition: 0.3s;

            &:hover {
                scale: 1.08;
            }

            &:nth-child(3) {
                margin-right: 0;
            }

            img {
                width: 25px;
                height: 25px;
            }

        }
    }
}

/* search */
.search_ex {
    display: none;
    position: absolute;
    z-index: 1;
    width: 100%;
    height: 300px;
    background-color: $color;

    .search_inner {
        position: relative;
        width: 1300px;
        margin: 0 auto;
        text-align: center;
        box-sizing: border-box;


        button {
            position: absolute;
            font-family: $font-family;
            top: 30px;
            right: 30px;
            border: none;
            background: none;
            font-size: 25px;
            cursor: pointer;
        }

        .logo {
            width: 185px;
            margin-top: 10px;
            margin-left: 45px;
            text-align: center;
            color: #222;
            cursor: pointer;

            p {
                font-size: 30px;
                font-weight: bold;
                /* letter-spacing: 3px; */
            }

            span {
                font-size: 12px;
                font-weight: bold;
                letter-spacing: 8px;
            }
        }

        input {
            font-family: $font-family;
            font-size: 20px;
            width: 990px;
            border: none;
            margin-top: 60px;
            border-bottom: solid #222 1px;
            padding-bottom: 10px;
            padding-left: 40px;
            box-sizing: border-box;
            background: none;


            &::placeholder {
                margin-left: 50px;
                color: #999;
            }

            &:focus {
                outline: none;
            }
        }

        img {
            position: absolute;
            top: 125px;
            right: 170px;
            width: 25px;
            height: 25px;
            cursor: pointer;

            &:hover {
                scale: 1.05;
            }
        }

        label {
            position: absolute;
            color: #aaa;
            left: 270px;
        }

        .search_txt {
            margin: 0 auto;
            margin-top: 40px;
            width: 1000px;
            display: flex;
            justify-content: space-between;

            p {
                font-family: $font-family;
                height: 40px;
                background-color: #fff;
                line-height: 40px;
                padding: 0 15px;
                border: 1px solid #222;
                border-radius: 3px;
                cursor: pointer;

                &:hover {
                    background-color: #222;
                    color: #fff;
                }
            }
        }
    }
}

/* 여기까지 header */

.wrap {
    position: relative;
    width: 1300px;
    margin: 0 auto;
    margin-top: 200px;
    margin-bottom: 50px;
    overflow: hidden;

    >h5 {
        font-size: 14px;
        letter-spacing: 1px;
        margin-bottom: 30px;
    }

    .content {
        display: flex;
        overflow: hidden;
        width: 1300px;
        height: 620px;
        padding: 0 0 10px 10px;
        box-sizing: border-box;
        padding-top: 0;
        background-color: #fff;

        .content_left {
            width: 750px;
            height: 620px;
            padding-right: 10px;

            .newSwiper1 {
                width: 750px;
                height: 450px;
                text-align: center;

                >.swiper-wrapper {
                    >.swiper-slide {
                        width: 450px;
                        height: 450px;

                        >img {
                            width: 60%;
                            transform: translateX(-20px);
                            filter: drop-shadow(2px 2px 2px #aaa);

                        }
                    }
                }
            }

            .newSwiper2 {
                >.swiper-wrapper {
                    .swiper-slide {
                        margin-top: 30px;
                        width: 130px;
                        height: 130px;
                        text-align: center;
                        background-color: #f7f7f7;
                        cursor: pointer;

                        >img {
                            width: 85%;
                            opacity: 0.6;
                        }
                    }

                    .swiper-slide-thumb-active {
                        background-color: #fff;

                        >img {
                            opacity: 1;
                            filter: drop-shadow(2px 2px 2px #aaa);
                        }
                    }
                }
            }
        }

        .content_right {
            /* padding-right: 45px; box-sizing: border-box; */
            border-left: 1px solid #ddd;
            width: 528px;
            height: 620px;
            text-align: center;

            >h5 {
                font-size: 16px;
                margin-top: 100px;
                margin-bottom: 35px;

                &:nth-child(2) {
                    margin-top: 10px;
                    margin-bottom: 15px;
                }
            }

            >p {
                font-family: $font-family;
                margin-bottom: 35px;
                font-size: 14px;

                &:nth-child(4) {
                    margin-bottom: 10px;
                }
            }

            .selectBox2 {
                position: relative;
                width: 485px;
                height: 50px;
                margin: 0 auto;
                text-align: left;
                border-radius: 4px;
                border: 2px solid #222;
                cursor: pointer;

                .label {
                    height: 50px;
                    padding-right: 300px;
                    border: 0 none;
                    outline: 0 none;
                    padding-left: 15px;
                    background: transparent;
                    cursor: pointer;
                    font-size: 16px;

                    &:hover {
                        color: #999;
                    }

                    >img {
                        position: absolute;
                        z-index: 999;
                        right: -30px;
                        top: -15px;
                        scale: 0.15;

                        &:hover {
                            filter: invert(50%);
                        }
                    }
                }

                .optionList {
                    display: none;
                    position: absolute;
                    top: -150px;
                    left: 0;
                    width: 490px;
                    background: #fff;
                    color: #222;
                    border: 1px solid #222;
                    border-radius: 6px;
                    transition: 0.3s;

                    .optionItem {
                        text-align: center;
                        border-bottom: 1px solid #222;
                        padding: 12px 0;

                        >span {
                            margin-left: 80px;
                        }

                        &:nth-child(3) {
                            border: none;
                        }

                        &:hover {
                            background: #ddd;
                        }
                    }

                }

            }

            >button {
                margin-top: 20px;
                width: calc((100% - 45px) / 2);
                height: 50px;
                background: transparent;
                border: 1px solid #222;
                border-radius: 5px;
                font-size: 16px;
                cursor: pointer;

                &:hover {
                    color: #555;
                    border: 1px solid #999;
                }
            }

            .buy {
                background-color: #222;
                color: #fff;

                &:hover {
                    background-color: #555;
                    color: #fff;
                }
            }
        }
    }

    .container {
        width: 1080px;
        margin: 0 auto;
        border-top: 1px solid #222;
        margin-top: 40px;
        margin-bottom: 100px;


        .page {
