                            }

                            .swiper-button-next {
                                color: #fff;
                            }
                        }

                        .next_back {
                            position: absolute;
                            top: 0;
                            right: 0;
                            width: 35px;
                            height: 150px;
                            background-color: #fff;
                            opacity: 0.8;

                        }

                        .swiper-button-next {
                            scale: 0.6;
                            margin-right: -8px;
                            color: #222;
                        }
                    }

                    .prev_button {
                        &:hover {
                            .prev_back {
                                background-color: #999;
                            }

                            .swiper-button-prev {
                                color: #fff;
                            }
                        }

                        .prev_back {
                            position: absolute;
                            top: 0;
                            left: 0;
                            width: 35px;
                            height: 150px;
                            background-color: #fff;
                            opacity: 0.8;
                        }

                        .swiper-button-prev {
                            scale: 0.6;
                            margin-left: -8px;
                            color: #222;
                        }

                    }

                }

                .review_write {
                    display: none;
                    position: absolute;
                    top: 0;
                    left: 30%;
                    width: 500px;
                    height: 350px;
                    border-radius: 3px;
                    background-color: #fff;
                    text-align: center;
                    z-index: 999;
                    box-shadow: 2px 2px 2px 2px #999;

                    >h1 {
                        margin: 20px 0;
                    }

                    .reviewform {
                        .review_name {
                            >input {
                                width: 300px;
                                height: 20px;
                                margin-bottom: 10px;
                                border-radius: 3px;
                                border: none;
                                background-color: #eee;
                                padding-left: 10px;
                                box-sizing: border-box;
                            }
                        }

                        .review_tit {
                            >input {
                                width: 300px;
                                height: 20px;
                                margin-bottom: 10px;
                                border-radius: 3px;
                                border: none;
                                background-color: #eee;
                                padding-left: 10px;
                                box-sizing: border-box;

                            }
                        }

                        .review_contents {
                            >textarea {
                                width: 300px;
                                height: 150px;
                                border: none;
                                background-color: #eee;
                                padding: 10px;
                                box-sizing: border-box;
                            }
                        }

                        .cmd {
                            margin-top: 10px;

                            >input {
                                width: 100px;
                                height: 25px;
                                border: none;
                                background-color: #222;
                                border-radius: 3px;
                                color: #fff;
                                line-height: 25px;
                                cursor: pointer;

                            }
                        }

                    }


                }

            }
        }

        /* page03 */
        .page04 {
            border: none;
            overflow: hidden;

            .recommend {
                .recommend_box {
                    float: left;
                    width: 255px;
                    height: 417px;
                    padding: 20px;
                    box-sizing: border-box;
                    margin-right: 20px;
                    background-color: #fff;
                    text-align: center;
                    cursor: pointer;
                    transition: 0.3s;

                    &:hover {
                        >div {
                            >img {
                                scale: 1.05;
                                /*  filter: drop-shadow(5px 2px 2px #999); */
                            }
                        }
                    }

                    >div {
                        width: 210px;
                        height: 255px;
                        margin-bottom: 10px;

                        >img {
                            width: 100%;
                        }
                    }

                    >h5 {
                        margin-bottom: 10px;

                        >span {
                            margin-left: 40px;
                        }
                    }

                    >p {
                        font-family: $font-family;
                        margin-bottom: 30px;
                    }

                    &:nth-child(4) {
                        margin-right: 0;

                        >div {
                            >img {
                                transform: translateY(20px);
                            }
                        }
                    }

                }

            }

        }

    }
}

/********************** cart_pop ********************/
.cart_pop {
    position: fixed;
    top: 40px;
    right: -700px;
    width: 530px;
    height: 868px;
    background-color: #fff;
    box-sizing: border-box;
    box-shadow: 2px 2px 2px 2px #999;
    z-index: 999;

    .cart_pop_inner {
        width: 500px;
        height: 835px;
        margin: 0 auto;
        margin-top: 15px;
        background-color: initial;
        border: 1px solid #222;
        text-align: center;

        >p {
            margin-top: 250px;
            font-family: $font-family;
        }

        >div {
            display: flex;
            justify-content: center;

            &:nth-child(2) {
                margin-top: 70px;

                >div {
                    &:nth-child(1) {
                        width: 110px;
                        height: 180px;
                        overflow: hidden;

                        >img {
                            width: 180%;
                            transform: translate(-23%, -7%);
                        }
                    }

                    &:nth-child(2) {
                        margin-left: 20px;
                        padding-top: 60px;
                        box-sizing: border-box;

                        >p {
                            margin-top: 5px;
                            font-size: 14px;
                            font-family: $font-family;
                        }

                        >h5 {
                            &:nth-child(3) {
                                margin-top: 20px;
                            }
                        }
                    }
                }

            }

            &:nth-child(3) {
                display: flex;
                justify-content: space-between;
                margin-top: 30px;
                margin: 0 auto;
                margin-top: 30px;
                width: 305px;
                line-height: 30px;

                .quantity_control {
                    display: flex;
                    align-items: center;
                    border: 1px solid #222;
                    border-radius: 3px;

                    >button {
                        width: 30px;
                        height: 30px;
                        color: #000;
                        font-size: 16px;
                        cursor: pointer;

                        &:hover {
                            color: #999;
                        }

                    }

                    >input {
                        width: 50px;
                        height: 30px;
                        border: none;
                        text-align: center;
                    }
                }

            }
        }

        >button {
            font-family: $font-family;
            width: 330px;
            height: 45px;
            border-radius: 3px;

            &:nth-child(4) {
                margin-top: 60px;
                border: 1px solid #222;
            }

            &:nth-child(5) {
                margin-top: 10px;
                background-color: #222;
                color: #fff;
            }

            &:nth-child(6) {
                position: absolute;
                width: 50px;
                height: 50px;
                top: 30px;
                right: 30px;
                font-size: 25px;

                &:hover {
                    color: #999;
                }
            }
        }
    }
}























/********************** instar_swiper ********************/


.insta_tit {
    font-size: 30px;
    margin-bottom: 20px;
}

.contents05 {
    width: 1300px;
    height: 280px;
    background-color: $color;

    .instar {
        width: 100%;
        height: 100%;

        .swiper-wrapper {
            position: relative;

            .swiper-slide {
                width: 280px;
                height: 280px;
                text-align: center;
                font-size: 18px;
                background-color: #ddd;
                display: flex;
                justify-content: center;
                align-items: center;
                /* overflow: hidden; */
                cursor: pointer;

                &:hover>img {
                    transform: translateY(-5px);
                }

                &:hover .insta_txt {
                    bottom: 0;
                }

                .shopping_icon {
                    position: absolute;
                    top: 10px;
                    right: 10px;
                    width: 30px;
                    height: 30px;
                    background-color: #000;
                    border-radius: 50%;

                    &:hover {
                        background-color: #fff;

                        img {
                            filter: invert(100%);
                        }
                    }

                    img {
                        width: 100%;
                    }
                }

                >img {
                    display: block;
                    width: 100%;
                    height: 100%;
                    object-fit: cover;
                    transition: 0.3s;
                }

                .insta_txt {
                    position: absolute;
                    width: 250px;
                    height: 70px;
                    bottom: -70px;
                    transition: 0.3s;

                    .insta_txt_bg {
                        position: absolute;
                        top: 0;
                        background-color: #222;
                        opacity: 0.5;
                        width: 250px;
                        height: 70px;
                        border-radius: 30px 30px 0 0;
                    }

                    .insta_id {
                        position: absolute;
                        left: 50%;
                        transform: translateX(-50%);
                        margin-top: 15px;
                        color: #fff;
                        position: absolute;
                        letter-spacing: 1px;
                        font-size: 14px;
                    }

                    .insta_text {
                        position: absolute;
                        margin-top: 40px;
                        left: 50%;
                        transform: translateX(-50%);
                        color: #fff;
                        font-size: 12px;
                        width: 200px;
                        overflow: hidden;
                        font-family: $font-family;
                        text-overflow: ellipsis;
                        white-space: nowrap;
                    }
                }
            }
        }

        .next_button {
            .next_back {
                position: absolute;
                top: 0;
                right: 0;
                z-index: 1;
                width: 80px;
                height: 100%;
                background-color: #fff;
                opacity: 0.5;
            }

            .swiper-button-next {
                margin-right: 10px;
                color: #222;
            }
        }

        .prev_button {
            .prev_back {
                position: absolute;
                top: 0;
                left: 0;
                z-index: 1;
                width: 80px;
                height: 100%;
                background-color: #fff;
                opacity: 0.5;
            }

            .swiper-button-prev {
                margin-left: 10px;
                color: #222;
            }
        }
    }

}


