        position: relative;
        height: 300px;
        border: 2px solid #ddd;

        ul {
            margin: 35px 0 0 115px;

            a {
                border-bottom: 1px solid #fff;
            }
        }

        .footer_menu {
            display: flex;

            li {
                margin-right: 30px;

                &:hover {
                    >a {
                        color: #999;
                    }
                }

                &:nth-child(1) a,
                &:nth-child(2) a,
                &:nth-child(3) a {
                    border: none;
                }

                a {
                    font-size: 14px;
                    font-family: $font-family;
                    color: #fff;

                }

                >div {
                    position: absolute;
                    z-index: 2;
                    top: -190px;
                    left: 85px;
                    width: 120px;
                    background-color: #444;
                    text-align: center;
                    /*  border-radius: 5px; */

                    >p {
                        height: 30px;
                        line-height: 30px;
                        border-bottom: 1px solid #666;
                        font-size: 14px;
                        font-family: $font-family;
                        color: #ddd;
                        cursor: pointer;

                        &:last-child {
                            /*  border: none; */
                        }

                        &:hover {
                            background-color: #999;
                        }
                    }
                }

                .service {
                    display: none;
                }

                .company {
                    display: none;
                    top: -32px;
                    left: 180px;
                }

                .sns {
                    display: none;
                    top: -64px;
                    left: 270px;
                }

            }
        }

        .footer_txt {

            >p,
            a {
                font-size: 14px;
                font-family: $font-family;
                color: #fff;
                line-height: 25px;

                &:nth-child(5) {
                    margin-top: 40px;
                }

                span {
                    margin-left: 30px;
                    font-family: $font-family;
                }
            }
        }

        .footer_logo {
            position: absolute;
            top: 40px;
            right: 150px;
            width: 125px;
            text-align: center;

            >div {
                width: 125px;
                height: 60px;
                border: 1px solid #fff;

                >h5 {
                    margin-top: 5px;
                    font-size: 12px;
                    color: #fff;
                    padding-bottom: 3px;
                    box-sizing: border-box;
                    border-bottom: 1px solid #fff;
                    letter-spacing: 1px;

